function chart(d3, dataUri, element, width, height) {
  // Set the dimensions and margins of the graph
  const margin = {top: 20, right: 20, bottom: 50, left: 60};
  const innerWidth = width - margin.left - margin.right;
  const innerHeight = height - margin.top - margin.bottom;

  // Append the svg object to the passed element
  const svg = d3.select(element).append("svg")
    .attr("width", width)
    .attr("height", height)
    .append("g")
    .attr("transform", `translate(${margin.left},${margin.top})`);

  // Load the data from the URI
  d3.csv(dataUri).then(function(data) {
    // Parse the data
    data.forEach(d => {
      d.Engine_Size__l_ = +d.Engine_Size__l_;
      d.Highway_Miles_Per_Gallon = +d.Highway_Miles_Per_Gallon;
    });

    // Filter out any invalid data points
    data = data.filter(d => d.Highway_Miles_Per_Gallon > 0 && d.Highway_Miles_Per_Gallon <= 100);

    // X scale and Axis
    const x = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.Engine_Size__l_)])
      .range([0, innerWidth]);
    svg.append("g")
      .attr("transform", `translate(0,${innerHeight})`)
      .call(d3.axisBottom(x));

    // Y scale and Axis
    const y = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.Highway_Miles_Per_Gallon)])
      .range([innerHeight, 0]);
    svg.append("g")
      .call(d3.axisLeft(y));

    // Add X axis label
    svg.append("text")
      .attr("text-anchor", "middle")
      .attr("x", innerWidth / 2)
      .attr("y", height - margin.bottom / 2)
      .text("Engine Size (L)");

    // Add Y axis label
    svg.append("text")
      .attr("text-anchor", "middle")
      .attr("transform", "rotate(-90)")
      .attr("y", -margin.left + 20)
      .attr("x", -innerHeight / 2)
      .text("Highway MPG");

    // Add dots
    svg.append('g')
      .selectAll("dot")
      .data(data)
      .join("circle")
        .attr("cx", d => x(d.Engine_Size__l_))
        .attr("cy", d => y(d.Highway_Miles_Per_Gallon))
        .attr("r", 3)
        .style("fill", "#69b3a2");

    // Add Chart Title
    svg.append("text")
      .attr("x", innerWidth / 2)
      .attr("y", -margin.top / 2)
      .attr("text-anchor", "middle")
      .style("font-size", "16px")
      .style("font-weight", "bold")
      .text("Engine Size vs Highway MPG");
  }).catch(function(error) {
    console.log("Error loading the CSV file:", error);
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", height / 2)
      .attr("text-anchor", "middle")
      .style("fill", "red")
      .text("Error loading data");
  });
}
